import React,{useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';


export const Balance = () => {

    const {transactions} = useContext(GlobalContext);

    const amounts = transactions.map(transaction => transaction.t);

    return (
        <>
           <h4>Your Balance </h4> 
            <h1>${amounts}</h1> 
        </>
    )
}
