import React, { createContext, useReducer } from 'react';

// Import the Reducer
import AppReducer from './AppReducer'

// Create the initial state
const initialTransactions  = [
        {id: 1, description: "Salary", transactionAmount: 7000},
        {id: 2, description: "Gas Bill", transactionAmount: -100},
        {id: 3, description: "Profit", transactionAmount: 2000},
        {id: 4, description: "School Fee", transactionAmount: -1500}
    ]

// Create the Global Context
export const GlobalState = createContext(initialTransactions);

// Create a Provider for the Global Context
export const GlobalProvider = ({ children }) => {

    const [state, dispatch] = useReducer(AppReducer, initialTransactions);

    // Delete Existing Transaction Action
    function delTransaction(id) {
        dispatch({
            type: 'DELETE_TRANSACTION',
            payload: id
        });
    }

    // Add New Transaction Action
    /*
    function addTransaction(transaction) {

        dispatch({
            type: 'ADD_TRANSACTION',
            payload: transaction
        })
    }
    */
    function addTransaction(transObj){
        //alert('in addTransaction of GlobalState')
        dispatch({
            type: "ADD_TRANSACTION",
            payload: { 
                id:transObj.id,
                transactionAmount: transObj.amount, 
                description: transObj.desc 
            }
        })
    }



    return (
        <GlobalState.Provider value={
            {
                initialTransactions:   initialTransactions,
                delTransaction: delTransaction,
                addTransaction
            }
        }>
            {children}
        </GlobalState.Provider>
    )
}
